import React from "react";
import { useNavigate } from "react-router-dom";
import { useEffect } from "react";
import { Link } from "react-router-dom";
import "./LandingPage.css"; 

const LandingPage = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const link = document.createElement("link");
    link.rel = "stylesheet";
    link.href =
      "https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css";
    document.head.appendChild(link);

    return () => {
      document.head.removeChild(link); // Cleanup when navigating away
    };
  }, []);

  const handleAnticipatoryBailClick = () => {
    navigate("/form"); // Navigate to the form page for Anticipatory Bail
  };

  return (
    <div className="">
      <header className="page-header">
        <div className="nav-container">
          <div className="logo-container">
            <span className="logo-gradient">Lawroom AI</span>
          </div>
              {/* Uncomment below if you want nav buttons in the future */}
              {/* <nav className="nav-links">
              <a href="#">About</a>
              <a href="#">Contact</a>
              </nav> */}
            </div>
                {/* Mobile menu button */}
                {/* <div className="md:hidden">
                  <button
                    onClick={toggleMobileMenu}
                    className="p-2 text-gray-600 hover:text-lawai-teal transition-colors"
                  >
                    {mobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
                  </button>
                </div> */}

                    {/* Desktop navigation */}
                    <div className="nav-container-wrapper">
                      <Link
                        to="/"
                        className="font-medium text-gray-600 hover:text-lawai-teal transition-colors"
                      >
                        Home
                      </Link>
                      <Link
                        to="/chat"
                        className="font-medium text-gray-600 hover:text-lawai-teal transition-colors"
                      >
                        Legal AI Chat
                      </Link>
                      <Link
                        to="/smart-drafting"
                        className="font-medium text-gray-600 hover:text-lawai-teal transition-colors py-2"
                      >
                        Smart Drafting
                      </Link>
                      <Link
                        to="/upcoming-features"
                        className="font-medium text-gray-600 hover:text-lawai-teal transition-colors py-2"
                      >
                        Upcoming Features
                      </Link>
                    </div>
            </header>
            <main className="main-content">
              <div className="intro-card">
                <h2>Smarter Legal Drafting, Powered by AI</h2>
                <h4 className="text-lg font-semibold mb-1">🌐 Now Drafting For:</h4>
                <p className="font-medium">State of Haryana & Chandigarh</p>
                <p className="mt-2 italic text-sm text-blue-700">
                  Stay tuned — support for more courts is coming soon!
                </p>
              </div>
              <div className="button-container">
                <button
                  onClick={handleAnticipatoryBailClick}
                  className="application-btn main-btn"
                >
                  Anticipatory Bail Application
                </button>
                <button className="application-btn">Civil Writ Application</button>
                <button className="application-btn">Bail Application</button>
              </div>
              <div>
                <h4 className="text-lg font-semibold mb-1">🌐 Now Drafting For:</h4>
                <p className="font-medium">State of Haryana & Chandigarh</p>
                 <p className="mt-2 italic text-sm text-blue-700">
                  Stay tuned — support for more courts is coming soon!
                </p> */}
              </div>
            </main>
            {/* <footer className="footer">
              Made In India
              <br />
              An Initiative by Law Wave Innovation Pvt. Ltd.
            </footer> */}
          </div>
        );
      };

      export default LandingPage;
