import React from "react";
import { useNavigate } from "react-router-dom";
import { useEffect } from "react";
import "./LandingPage.css"; // Import the CSS file

const LandingPage = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const link = document.createElement("link");
    link.rel = "stylesheet";
    link.href = "https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css";
    document.head.appendChild(link);

    return () => {
      document.head.removeChild(link); // Cleanup when navigating away
    };
  }, []);

  const handleAnticipatoryBailClick = () => {
    navigate("/form"); // Navigate to the form page for Anticipatory Bail
  };

  return (
    <div className="landing-container">
      <header className="page-header">
        <div className="logo-container">
          <h1 className="logo-text">Lawroom <span className="ai-text">AI</span></h1>
        </div>
      </header>
      <main className="main-content">
        <div className="intro-card">
          <h2>Indian Legal Research and Services, Reimagined with AI</h2>
          <p>Get instant, accurate answers to your legal questions. Lawroom AI is built specifically for Indian law, making legal research faster and more accessible.</p>
        </div>
        <div className="button-container">
          <button onClick={handleAnticipatoryBailClick} className="application-btn main-btn">
            Anticipatory Bail Application
          </button>
          <button className="application-btn">Civil Writ Application</button>
          <button className="application-btn">Bail Application</button>
        </div>
      </main>
      <footer className="footer">
        Made In India
        <br />
        An Initiative by Law Wave Innovation Pvt. Ltd.
      </footer>
    </div>
  );
};

export default LandingPage;
